import { LOGIN } from "./login";

const initialState = {
    user: {
        email:"",
        name:"",
        password:""
    }
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                user: action.payload
            }
            break;
    
        default: return state;
    }
}

export default userReducer
