import { LOGIN } from "./login";

const initialState = {
    user:null
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                users: state
            }
            break;
    
        default:
            break;
    }
    state.user = action.payload
}


export default userReducer
