import { LOGIN } from "./login";

const initialState = {
    user: {
        email:"",
        name:"",
        password:""
    }
}

const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                user: {
                    email:action.payload.email,
                    name:state.payload.name,
                    password:state.payload.password
                }
            }
            break;
    
        default: return state;
    }
}

export default userReducer
